mapper.insert:
INSERT INTO
name,area,datacenter,position,height,brand,type, serialnumber,bIp,mgmtIp,cpuname,cpunum,memorysum,storage, manufactor,department,user,maintainor
#{name, jdbcType=VARCHAR}, #{area, jdbcType=VARCHAR}, #{datacenter, jdbcType=VARCHAR}, #{position, jdbcType=VARCHAR}, #{height, jdbcType=VARCHAR},#{brand, jdbcType=VARCHAR}, #{type, jdbcType=VARCHAR}, #{serialnumber, jdbcType=VARCHAR}, #{bIp, jdbcType=VARCHAR}, #{mgmtIp, jdbcType=VARCHAR}, #{cpuname, jdbcType=VARCHAR}, #{cpunum, jdbcType=INTEGER}, #{memorysum, jdbcType=INTEGER}, #{storage, jdbcType=INTEGER}, #{manufactor, jdbcType=VARCHAR}, #{department, jdbcType=VARCHAR}, #{user, jdbcType=VARCHAR},#{maintainor, jdbcType=VARCHAR}

mapper.update:
UPDATE
name=#{name}, area=#{area}, datacenter=#{datacenter}, position=#{position}, height=#{height}, brand=#{brand}, type=#{type}, serialnumber=#{serialNumber}, bIp=#{bIp}, mgmtIp=#{mgmtIp}, cpuname=#{cpuName}, cpunum=#{cpuNum}, memorysum=#{memorySum}, storage=#{storage}, manufactor=#{manufactor}, department=#{department}, user=#{user}, maintainor=#{maintainor}
WHERE id = #{id, jdbcType=BIGINT}

mapper.select:
SELECT

FROM
 AS p


        AND p.name LIKE CONCAT('%',#{name, jdbcType=VARCHAR},'%')


        AND p.area LIKE CONCAT('%',#{area, jdbcType=VARCHAR},'%')


        AND p.datacenter = #{datacenter, jdbcType=VARCHAR}


        AND p.position LIKE CONCAT('%',#{position, jdbcType=VARCHAR},'%')


        AND p.brand LIKE CONCAT('%',#{brand, jdbcType=VARCHAR},'%')


        AND p.serialNumber = #{serialNumber, jdbcType=VARCHAR}


        AND p.bIp = #{bIp, jdbcType=VARCHAR}


        AND p.mgmtIp = #{mgmtIp, jdbcType=VARCHAR}


ORDER BY p.id ASC
